import React, {useState, useEffect} from "react"
import * as fcl from "@onflow/fcl"

export function AuthCluster() {
  const [user, setUser] = useState({loggedIn: null})
  useEffect(() => fcl.currentUser().subscribe(setUser), [])

  if (user.loggedIn) {
    return (
      <div>
          <ul>
            <li><span class="active">{user?.addr ?? "No Address"}</span></li>
            <li><span onClick={fcl.unauthenticate}>Log Out</span></li>
        </ul>

      </div>
    )
  } else {
    return (
        <ul>
            <li><span class="active" onClick={fcl.logIn}>Log In</span></li>
            <li><span onClick={fcl.signUp}>Sign Up</span></li>
        </ul>
    )
  }
}

export default AuthCluster;
