// File: ./src/auth-cluster.js

import React, {useState, useEffect} from "react"
import {useCurrentUser} from "./hooks/current-user"
import "./AuthCluster.scss"
function WithAuth() {
  const cu = useCurrentUser()

  return !cu.loggedIn ? null : (
    <header>
    <div class="container">
      <nav id="navigation">
        <a href="#" class="logo">NFTune</a>
        <a aria-label="mobile menu" class="nav-toggle">
          <span></span>
          <span></span>
          <span></span>
        </a>
          <ul class="menu-left">
            <li><a href="#">{cu.addr ?? "No Address"}</a></li>
            <li><a onClick={cu.logOut}>Logout</a></li>
          </ul>
      </nav>
    </div>
    </header>

    
  )
}

function SansAuth() {
  const cu = useCurrentUser()

  return cu.loggedIn ? null : (
    <div>
      <button onClick={cu.logIn}>Log In</button>
      <button onClick={cu.signUp}>Sign Up</button>
    </div>
  )
}

export function AuthCluster() {
  return (
    <>
      <WithAuth />
      <SansAuth />
    </>
  )
}
